XTextDocument.ScriptText:
// 对所有文档节都有效的事件响应JavaScript

function _XTextSectionElement_GotFocus( eventSender , Args ) 
{
	eventSender.Style.BackgroundColor = Color.Yellow;
	eventSender.InvalidateView();
}

function _XTextSectionElement_LostFocus( eventSender , Args )
{
	eventSender.Style.BackgroundColor = Color.Transparent;
	eventSender.InvalidateView();

}

function _XTextSectionElement_MouseDblClick( eventSender , Args ){
	window.alert( document.FileName );
}
